let translation = [];

$.ajax({
    type: "GET",
    url: "getTranslations",
    success: function (res) {
        translation = res;
        let validator = $("#contactForm").validate({
            rules: {
                name: {
                    required: true
                },
                email: {
                    required: true,
                    email: true
                },
                subject: {
                    required: true,
                },
                message: {
                    required: true
                }
            },
            messages: {
                name: {
                    required: translation['name-validation-message']
                },
                email: {
                    required: translation['email-validation-message'],
                    email: translation['invalid-email-address']
                },
                subject: {
                    required: translation['subject-validation-message']
                },
                message: {
                    required: translation['message-validation-message']
                },
            }
        });
    }
});


