function submitForm() {

    if (!$('#email').valid() || !$('#name').valid() || !$('#msg_subject').valid() || !$('#message').valid()) {
        formError();
        submitMSG(false, translation['contact-form-validation-error']);
        return;
    }

    $("#form-submit").prop('disabled', true);
    // Initiate Variables With Form Content
    var name = $("#name").val();
    var email = $("#email").val();
    var msg_subject = $("#msg_subject").val();
    var message = $("#message").val();

    $.ajaxSetup({
        headers: {
            'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
        }
    });

    $.ajax({
        type: "POST",
        url: "contact",
        data: "name=" + name + "&email=" + email + "&msg_subject=" + msg_subject + "&message=" + message,
        success: function (res) {
            $("#form-submit").prop('disabled', false);
            if (res.status == "success") {
                formSuccess();
            } else {
                formError();
                submitMSG(false, translation['contact-form-something-went-wrong']);
            }
        },
        error: function (err) {
            $("#form-submit").prop('disabled', false);
            formError();
            submitMSG(false, translation['contact-form-something-went-wrong']);
        }
    });
}

function subscribeToNewsletter() {
    let email = $("#subscribe-email");

    $.ajaxSetup({
        headers: {
            'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
        }
    });

    $.ajax({
        type: "POST",
        url: "/subscribe",
        data: {email: email.val()},
        success : function(res){
            email.val("");
            $("#subscribe-message").html("Success.")
        },
        error: function (err) {
            $("#subscribe-message").html("Something went wrong :(")
        }
    });
}

function formSuccess() {
    $("#contactForm")[0].reset();
    submitMSG(true, translation['contact-form-success'])
}

function formError() {
    $("#contactForm").removeClass().addClass('shake animated').one('webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend', function () {
        $(this).removeClass();
    });
}

function submitMSG(valid, msg) {
    if (valid) {
        var msgClasses = "h5 tada animated text-success";
    } else {
        var msgClasses = "h5 text-danger";
    }

    $("#msgSubmit").removeClass().addClass(msgClasses).text(msg);
}
